mapper.select:
select * from (select f.flight_id, f.flight_no, f.flight_date, f.flight_dep, f.flight_arr, f.real_landing_time, f.real_take_off_time,
f.plan_take_off_time, f.plan_landing_time, f.flight_status, f.boarding_port, f.board_status, s.schedule_time, s.name as schedule_event_name
from flight as f right join (select fse.flight_id, se.schedule_event_id, se.schedule_time, se.name from flight_schedule_event as fse left join
(select schedule_event_id, schedule_time, name from schedule_event where airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and is_deleted = 0)
as se on fse.schedule_event_id =se.schedule_event_id
where fse.airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and fse.is_deleted = 0) as s on f.flight_id = s.flight_id
where f.airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and f.is_deleted = 0) as fs
right join (select oi.flight_id from order_info as oi right join (select order_id from order_service
where airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and is_deleted = 0

  and service_detail->>"$.serviceDetailId" = #{criteria.servId,jdbcType=BIGINT}
) as os on oi.order_id = os.order_id where oi.airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and oi.is_deleted = 0)
as o on fs.flight_id = o.flight_id
where 0 = 0

  and fs.flight_date = #{criteria.flightDate,jdbcType=DATE}


  and fs.flight_no = #{criteria.flightNo,jdbcType=VARCHAR}


  and fs.is_in_or_out = #{criteria.isInOrOut,jdbcType=SMALLINT}


  and fs.flight_status = #{criteria.flightStatus,jdbcType=SMALLINT}


  and fs.schedule_event_id = #{criteria.scheduleEventId,jdbcType=BIGINT}

order by fs.schedule_time desc,

  fs.plan_take_off_time desc


  fs.plan_landing_time desc

LIMIT #{page.startPageNo},#{page.pCount}

