mapper.select:
select s.schedule_event_id, f.schedule_time, s.name from schedule_event as s right join (select schedule_event_id, schedule_time from flight_schedule_event where airport_code = #{airportCode,jdbcType=VARCHAR} and flight_id = #{flightId,jdbcType=BIGINT} and is_deleted = 0) as f on s.schedule_event_id = f.schedule_event_id where s.airport_code = #{airportCode,jdbcType=VARCHAR} and s.is_deleted = 0
and s.schedule_event_id = #{scheduleEventId,jdbcType=BIGINT}
order by s.schedule_event_id desc

mapper.insert:
insert into schedule_event (schedule_event_id, no, type, 
  name, is_approach, uupdate_user
  )
values (#{scheduleEventId,jdbcType=BIGINT}, #{no,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{isApproach,jdbcType=BIT}, #{uupdateUser,jdbcType=BIGINT}
  )
