mapper.select:
select count(t.orderId) from
(
select o.order_id as orderId from order_info o
inner join flight f on o.flight_id = f.flight_id

and  o.is_deleted = 0 and f.is_deleted = 0 and o.airport_code = #{airportCode}

  and f.flight_date >= #{queryFlightDateBegin}


  and f.flight_date <= #{queryFlightDateEnd}


  and o.create_time >= #{queryCreateDateBegin}


  and o.create_time <= #{queryCreateDateEnd}


  and o.create_time <= #{queryCreateDateEnd}


  and o.customer_name like concat('%',#{queryCustomerName},'%')


  and o.protocol_name like concat('%',#{queryProtocolName},'%')


  and o.product_name in (${queryProductId})


  and o.protocol_type in (${queryProtocolType})

GROUP BY o.customer_id,o.protocol_id,o.product_id
)t

select count(order_id) as orderCount,o.customer_id as customerId,o.customer_name as customerName,o.protocol_id as protocolId,o.protocol_name as protocolName,o.protocol_type as protocolType,o.product_name as productName
from order_info o
inner join flight f on o.flight_id = f.flight_id

where o.is_deleted = 0 and f.is_deleted = 0 and o.airport_code = #{criteria.airportCode}

  and f.flight_date >= #{criteria.queryFlightDateBegin}


  and f.flight_date <= #{criteria.queryFlightDateEnd}


  and o.create_time >= #{criteria.queryCreateDateBegin}


  and o.create_time <= #{criteria.queryCreateDateEnd}


  and o.customer_name like concat('%',#{criteria.queryCustomerName},'%')


  and o.protocol_name like concat('%',#{criteria.queryProtocolName},'%')


  and o.protocol_id in (${criteria.queryProtocolId})


  and o.product_name in (${criteria.queryProductId})


  and o.protocol_type in (${criteria.queryProtocolType})

GROUP BY o.customer_id,o.protocol_id,o.product_id
limit #{page.startPageNo},#{page.pCount}

select *,${criteria.totalAmount} from (
  select ${criteria.selectFields},o.customer_id,o.product_name,o.protocol_id,o.protocol_type
  from order_info o
  inner join flight f on f.flight_id = o.flight_id
  inner join passenger p on p.order_id = o.order_id
  where o.airport_code = #{criteria.airportCode} and o.is_deleted = 0 and o.order_status = '已使用'

    and f.flight_date >= #{criteria.queryFlightDateBegin}


    and f.flight_date <= #{criteria.queryFlightDateEnd}

  GROUP BY o.order_id
) t where 1 = 1 ${criteria.queryWhere}

select count(f.flight_id) from ( select o.flight_id,o.customer_id,o.customer_name, sum(os.service_detail->>'$.serverNum') as serverPersonNum,os.price_rule->>'$.price' as price from order_info o inner join order_service os on o.order_id = os.order_id and o.protocol_type in (9,10) and o.airport_code = #{airportCode} and o.is_deleted = 0 and os.service_detail->>'$.serviceId' = 5 and o.order_status = '已使用'
and o.customer_name like concat('%',#{queryCustomerName},'%')
GROUP BY o.flight_id,o.customer_id,o.customer_name ) t INNER JOIN flight f on f.flight_id = t.flight_id where 1 = 1
and f.flight_date >= #{queryFlightDateBegin}
and f.flight_date <= #{queryFlightDateEnd}

select f.flight_id as flightId,f.flight_date as flightDate, concat_ws(' - ',f.flight_depcode,f.flight_arrcode) as leg,f.flight_no as flightNo,f.plan_no as planNo,f.flight_depcode as flightDepcode,f.flight_arrcode as flightArrcode, t.customerId,t.customer_name as customerName, t.serverPersonNum,CAST(t.price AS SIGNED) as price,t.serverPersonNum * t.price as amount,t.airport_code as airportCode from ( select o.flight_id,o.customer_id as customerId,o.customer_name,o.airport_code, sum(os.service_detail->>'$.serverNum') as serverPersonNum,os.price_rule->>'$.price' as price from order_info o inner join order_service os on o.order_id = os.order_id and o.protocol_type in (9,10) and o.airport_code = #{criteria.airportCode} and o.is_deleted = 0 and os.service_detail->>'$.serviceId' = 5 and o.order_status = '已使用'
and o.customer_name like concat('%',#{criteria.queryCustomerName},'%')
GROUP BY o.flight_id,o.customer_id,o.customer_name,o.airport_code ) t INNER JOIN flight f on f.flight_id = t.flight_id where 1 = 1
and f.flight_date >= #{criteria.queryFlightDateBegin}
and f.flight_date <= #{criteria.queryFlightDateEnd}
limit #{page.startPageNo},#{page.pCount}

SELECT f.flight_date, f.plan_no, f.flight_no, f.flight_depcode, f.flight_arrcode, concat_ws(' - ',f.flight_dep,f.flight_arr) AS leg, o2.* FROM flight f INNER JOIN ( SELECT o1.*, os.service_detail ->> '$.serverNum' AS server_num, (SELECT COUNT(pp.passenger_id) FROM passenger pp WHERE pp.passenger_type = 0 AND pp.order_id = o1.order_id) AS person_num FROM ( SELECT oi.order_status, oi.protocol_type, oi.protocol_id, oi.protocol_name, oi.flight_id, oi.customer_name, p.sit_no, oi.order_id, oi.airport_code, p.NAME name, p.ticket_no, p.card_type, p.card_no, p.cabin_no, p.expire_time, p.passenger_type FROM order_info oi LEFT JOIN passenger p ON oi.order_id = p.order_id ) AS o1 INNER JOIN order_service os ON o1.order_id = os.order_id AND os.service_detail ->> '$.serviceId' = 5 ) o2 ON o2.flight_id = f.flight_id WHERE 1 = 1
AND f.flight_date >= #{param.queryFlightDateBegin}
AND f.flight_date <= #{param.queryFlightDateEnd}
AND o2.protocol_id = #{param.queryProtocolId}
AND o2.customer_name LIKE concat('%',#{param.queryCustomerName},'%')
AND o2.order_status = '已使用' AND o2.protocol_type = #{protocolType} AND o2.airport_code = #{param.airportCode}
