mapper.select:
select count(t.orderId) from
(
select o.order_id as orderId from order_info o
inner join flight f on o.flight_id = f.flight_id

and  o.is_deleted = 0 and f.is_deleted = 0 and o.airport_code = #{airportCode}

  and f.flight_date >= #{queryFlightDateBegin}


  and f.flight_date <= #{queryFlightDateEnd}


  and o.create_time >= #{queryCreateDateBegin}


  and o.create_time <= #{queryCreateDateEnd}


  and o.create_time <= #{queryCreateDateEnd}


  and o.customer_name like concat('%',#{queryCustomerName},'%')


  and o.protocol_name like concat('%',#{queryProtocolName},'%')


  and o.product_name in (${queryProductId})


  and o.protocol_type in (${queryProtocolType})

GROUP BY o.customer_id,o.protocol_id,o.product_id
)t

select count(order_id) as orderCount,o.customer_id as customerId,o.customer_name as customerName,o.protocol_id as protocolId,o.protocol_name as protocolName,o.protocol_type as protocolType,o.product_name as productName
from order_info o
inner join flight f on o.flight_id = f.flight_id

where o.is_deleted = 0 and f.is_deleted = 0 and o.airport_code = #{criteria.airportCode}

  and f.flight_date >= #{criteria.queryFlightDateBegin}


  and f.flight_date <= #{criteria.queryFlightDateEnd}


  and o.create_time >= #{criteria.queryCreateDateBegin}


  and o.create_time <= #{criteria.queryCreateDateEnd}


  and o.customer_name like concat('%',#{criteria.queryCustomerName},'%')


  and o.protocol_name like concat('%',#{criteria.queryProtocolName},'%')


  and o.protocol_id in (${criteria.queryProtocolId})


  and o.product_name in (${criteria.queryProductId})


  and o.protocol_type in (${criteria.queryProtocolType})

GROUP BY o.customer_id,o.protocol_id,o.product_id
limit #{page.startPageNo},#{page.pCount}

select count(f.flight_id) from ( select o.flight_id,o.customer_id,o.customer_name, sum(os.service_detail->>'$.serverNum') as serverPersonNum,os.price_rule->>'$.price' as price from order_info o inner join order_service os on o.order_id = os.order_id and o.protocol_type in (9,10) and o.airport_code = #{airportCode} and o.is_deleted = 0 and os.service_detail->>'$.serviceId' = 5
and o.customer_name like concat('%',#{queryCustomerName},'%')
GROUP BY o.flight_id,o.customer_id,o.customer_name,os.price_rule ) t INNER JOIN flight f on f.flight_id = t.flight_id where 1 = 1
and f.flight_date >= #{queryFlightDateBegin}
and f.flight_date <= #{queryFlightDateEnd}

select f.flight_id as flightId,f.flight_date as flightDate, concat_ws(' - ',f.flight_dep,f.flight_arr) as leg,f.flight_no as flightNo,f.plan_no as planNo,f.flight_depcode as flightDepcode,f.flight_arrcode as flightArrcode, t.customerId,t.customer_name as customerName, t.serverPersonNum,CAST(t.price AS SIGNED) as price,t.serverPersonNum * t.price as amount,t.airport_code as airportCode from ( select o.flight_id,o.customer_id as customerId,o.customer_name,o.airport_code, sum(os.service_detail->>'$.serverNum') as serverPersonNum,os.price_rule->>'$.price' as price from order_info o inner join order_service os on o.order_id = os.order_id and o.protocol_type in (9,10) and o.airport_code = #{criteria.airportCode} and o.is_deleted = 0 and os.service_detail->>'$.serviceId' = 5
and o.customer_name like concat('%',#{criteria.queryCustomerName},'%')
GROUP BY o.flight_id,o.customer_id,o.customer_name,o.airport_code,os.price_rule ) t INNER JOIN flight f on f.flight_id = t.flight_id where 1 = 1
and f.flight_date >= #{criteria.queryFlightDateBegin}
and f.flight_date <= #{criteria.queryFlightDateEnd}
limit #{page.startPageNo},#{page.pCount}
