mapper.select:
select count(orderFlightClientPassengerService.order_id) from ( select orderFlightClientPassenger.*,orderService.order_service_id,orderService.service_detail from ( select orderFlightClient.*,p.passenger_id,p.name as passenger_name,p.phone,p.identity_card,p.work_unit,p.vip_card as passenger_vip_card,p.sit_no as passenger_sit_no,p.cabin_no,p.ticket_no,p.card_no,p.card_type,p.expire_time from ( select concat(o.airport_code,lpad(o.order_id,6,'0')) AS order_no,o.order_id,o.protocol_id,o.customer_id,o.booking_person, o.notice_person,o.booking_way,o.is_important,o.product_id,o.print_check,o.print_check_remark,o.consign,o.consign_remark,o.is_deleted, o.other_remark,o.order_status,o.order_type,o.agent_person,o.server_card_no,o.vip_card,o.cash,o.sit_no,o.server_complete,o.server_person_num,o.airport_code, f.flight_id,f.flight_date,f.flight_no,f.flight_depcode,f.flight_arrcode,flight_dep,f.flight_arr,f.flight_dep_airport,flight_arr_airport,f.flight_position,f.board_in_out, f.plan_take_off_time,f.plan_landing_time,f.is_in_or_out,f.boarding_port,f.is_near_or_far,f.plan_no, ic.name as customer_name from order_info o inner join flight f on o.flight_id = f.flight_id inner JOIN institution_client ic on ic.institution_client_id = o.customer_id ) as orderFlightClient left join passenger p on orderFlightClient.order_id = p.order_id )as orderFlightClientPassenger left join order_service orderService on orderService.order_id = orderFlightClientPassenger.order_id ) as orderFlightClientPassengerService where orderFlightClientPassengerService.is_deleted = 0 and orderFlightClientPassengerService.airport_code = #{criteria.airportCode}
and orderFlightClientPassengerService.flight_no like concat('%',#{criteria.queryFlightNo},'%')
and orderFlightClientPassengerService.flight_date = #{criteria.queryFlightDate}
and orderFlightClientPassengerService.passenger_name like concat('%',#{criteria.queryPassengerName},'%')
and orderFlightClientPassengerService.identity_card = #{criteria.queryIdentityCard}
and orderFlightClientPassengerService.booking_person in ( #{criteria.queryBookingIds})
and orderFlightClientPassengerService.protocol_id in ( #{criteria.queryProtocolIds})
and orderFlightClientPassengerService.order_status in ( #{criteria.queryOrderStatus})
and orderFlightClientPassengerService.is_important in ( #{criteria.queryIsImportant})
and orderFlightClientPassengerService.order_type in ( #{criteria.queryOrderType})

select orderFlightClientPassengerService.* from ( select orderFlightClientPassenger.*,orderService.order_service_id,orderService.service_detail from ( select orderFlightClient.*,p.passenger_id,p.name as passenger_name,p.phone,p.identity_card,p.work_unit,p.vip_card as passenger_vip_card,p.sit_no as passenger_sit_no,p.cabin_no,p.ticket_no,p.card_no,p.card_type,p.expire_time from ( select concat(o.airport_code,lpad(o.order_id,6,'0')) AS order_no,o.order_id,o.protocol_id,o.customer_id,o.booking_person, o.notice_person,o.booking_way,o.is_important,o.product_id,o.print_check,o.print_check_remark,o.consign,o.consign_remark,o.is_deleted, o.other_remark,o.order_status,o.order_type,o.agent_person,o.server_card_no,o.vip_card,o.cash,o.sit_no,o.server_complete,o.server_person_num,o.airport_code, f.flight_id,f.flight_date,f.flight_no,f.flight_depcode,f.flight_arrcode,flight_dep,f.flight_arr,f.flight_dep_airport,flight_arr_airport,f.flight_position,f.board_in_out, f.plan_take_off_time,f.plan_landing_time,f.is_in_or_out,f.boarding_port,f.is_near_or_far,f.plan_no, ic.name as customer_name from order_info o inner join flight f on o.flight_id = f.flight_id and o.is_deleted = 0 inner JOIN institution_client ic on ic.institution_client_id = o.customer_id limit #{page.startPageNo},#{page.pCount} ) as orderFlightClient left join passenger p on orderFlightClient.order_id = p.order_id )as orderFlightClientPassenger left join order_service orderService on orderService.order_id = orderFlightClientPassenger.order_id ) as orderFlightClientPassengerService where orderFlightClientPassengerService.airport_code = #{criteria.airportCode}
and orderFlightClientPassengerService.flight_no like concat('%',#{criteria.queryFlightNo},'%')
and orderFlightClientPassengerService.flight_date = #{criteria.queryFlightDate}
and orderFlightClientPassengerService.passenger_name like concat('%',#{criteria.queryPassengerName},'%')
and orderFlightClientPassengerService.identity_card = #{criteria.queryIdentityCard}
and orderFlightClientPassengerService.booking_person in ( #{criteria.queryBookingIds})
and orderFlightClientPassengerService.protocol_id in ( #{criteria.queryProtocolIds})
and orderFlightClientPassengerService.order_status in ( #{criteria.queryOrderStatus})
and orderFlightClientPassengerService.is_important in ( #{criteria.queryIsImportant})
and orderFlightClientPassengerService.order_type in ( #{criteria.queryOrderType})

mapper.update:
UPDATE
  order_info SET server_complete = #{serverComplete}, update_time = NOW(), update_user = #{updateUser}
WHERE airport_code = #{airportCode} AND flight_id = #{flightId}
