mapper.select:
SELECT p.name,p.flight_id,p.order_id,concat(p.airport_code,lpad(p.order_id,6,'0')) AS order_no,sd.serviceId,sd.servId,sd.room_name,sd.serverNum,o.order_status,o.create_time,o.server_create_time,o.customer_name,o.is_important FROM passenger p LEFT JOIN (SELECT service_detail->>'$.serviceId' AS serviceId,service_detail->>'$.servId' AS servId,service_detail->>'$.serverNum' AS serverNum,service_detail->>'$.serviceDetailName' AS room_name,order_id,airport_code FROM order_service WHERE airport_code = #{airportCode} AND is_deleted = 0 ) sd ON p.order_id = sd.order_id LEFT JOIN (SELECT flight_id,order_status,create_time,order_id,airport_code,server_create_time,customer_name,is_important FROM order_info WHERE airport_code = #{airportCode} AND is_deleted = 0) o ON p.order_id = o.order_id
WHERE p.flight_id = #{flightId} and (order_status = '已预约' or order_status = '已使用')
AND servId = #{servId}
AND serviceId = #{typeId}
