mapper.update:
UPDATE product SET
product_name = #{productName},
update_time = NOW(), update_user = #{updateUser} WHERE product_id = #{productId} AND airport_code = #{airportCode}

UPDATE
  product
SET
  is_deleted = 1, update_time = NOW(), update_user = #{updateUser}
WHERE
  product_id = #{productId} AND airport_code = #{airportCode}

mapper.select:
SELECT count(product_id) as num FROM v_product WHERE airport_code = #{airportCode} AND is_deleted = 0
AND product_name = #{productName}
AND product_id != #{productId}
