mapper.update:

update authorizer


    name = #{name,jdbcType=VARCHAR},


    cellphone = #{cellphone,jdbcType=VARCHAR},


    telephone = #{telephone,jdbcType=VARCHAR},


    is_deleted = #{isDeleted,jdbcType=SMALLINT},

  update_time = CURRENT_TIMESTAMP

where airport_code = #{airportCode,jdbcType=VARCHAR}

  and protocol_id = #{protocolId,jdbcType=BIGINT}


  and authorizer_id = #{authorizerId,jdbcType=BIGINT}

mapper.select:
SELECT authorizer_id,name,cellphone,telephone FROM authorizer where airport_code = #{airportCode,jdbcType=VARCHAR} and is_deleted = 0
and protocol_id = #{protocolId,jdbcType=BIGINT}
order by authorizer_id desc
