mapper.select:
SELECT s.*,product_category,service_type FROM v_serv s left join product_type_allocation p on s.product_type_allocation_id = p.id where s.airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and s.is_deleted = 0
and p.product_category like concat('%',#{criteria.productCategory},'%')
and p.service_type = #{criteria.serviceType}
and s.name like concat('%',#{criteria.name},'%')
order by s.id desc LIMIT #{page.startPageNo},#{page.pCount}

SELECT product_category,service_type,s.id,s.airport_code,s.product_type_allocation_id,s.no,s.name as service_name,s.depart_type,s.terminal,s.is_security_check, s.available_gate,s.region,s.address_detail,s.create_time,s.update_time,s.is_deleted FROM v_serv s left join product_type_allocation p on s.product_type_allocation_id = p.id where s.airport_code = #{criteria.airportCode,jdbcType=VARCHAR} and s.product_type_allocation_id = #{criteria.productTypeAllocationId,jdbcType=BIGINT} and s.is_deleted = 0
and s.id not in (${criteria.ids})
order by s.id desc LIMIT #{page.startPageNo},#{page.pCount}
